mapper.update:

		update community
 		set comviews = comviews + 1
 		where comNum = #{comNum}


		update community c
 		set c.comreco = (select count(com_no)from comment1 where comnum = #{comNum})
 		where c.comNum = #{comNum}


		update community c
 		set c.com_re_lev = (select count(likeNum)from comlike where comnum = #{comNum})
 		where c.comNum = #{comNum}
